mapper.insert:
    	INSERT INTO 
    	       APPLE_PICTURE (
    	       BOARD_SEQ,
			   PICTURE_URL
			   )
	    VALUES(
	    	   #{board_seq},
	    	   #{picture_url}
		       )

		DELETE FROM APPLE_PICTURE
         WHERE 1=1
           AND BOARD_SEQ = #{board_seq}

mapper.update:
		UPDATE APPLE_BOARD
   		   SET BOARD_TITLE = #{board_title}
			 , BOARD_PRICE = #{board_price}
			 , USER_ID = #{user_id}
			 , BOARD_CONTENT = #{board_content}
			 , BOARD_DATE = TO_CHAR(SYSDATE,'yyyy.mm.dd hh24:mi')
         WHERE 1=1
   		   AND BOARD_SEQ = #{board_seq}
